// Problem: B. Swap and Delete// Contest: Codeforces - Educational Codeforces Round 160 (Rated for Div. 2)// URL: https://codeforces.com/problemset/problem/1913/B// VOIDHUNTER// // Powered by CP Editor (https://cpeditor.org)#include <bits/stdc++.h>using namespace std; // Fast I/O#define fastio() ios::sync_with_stdio(false); cin.tie(0); cout.tie(0); typedef long long ll;typedef unsigned long long ull;typedef long double ld;typedef pair<int, int> pii;typedef pair<ll, ll> pll;typedef vector<int> vi;typedef vector<ll> vll; // Macros#define all(x) x.begin(), x.end()#define pb push_back#define ff first#define ss second#define yes cout << "YES\n";#define no cout << "NO\n"; // Constantsconst int MOD = 1e9 + 7;const int INF = 1e9;const ll LINF = 1e18;const int N = 2e5 + 5; // Adjust based on problem constraints // Debugging // #define debug(x) cerr << #x << " = " << x << '\n'; //Additional helpers:ll gcd(ll a, ll b) { return b ? gcd(b, a % b) : a; }ll lcm(ll a, ll b) { return a / gcd(a, b) * b; }ll mod_add(ll a, ll b) { return (a + b) % MOD; }ll mod_sub(ll a, ll b) { return (a - b + MOD) % MOD; }ll mod_mul(ll a, ll b) { return (a * b) % MOD; }ll mod_pow(ll a, ll b) {    ll res = 1;    while (b) {        if (b & 1) res = mod_mul(res, a);        a = mod_mul(a, a);        b >>= 1;    }    return res;}    int main() {    fastio();        int t = 1;    cin >> t;    while (t--) {    	string s;cin>>s;		int zero=0;int one=0;		int n=s.size();		for (int i=0;i<n;i++){			if(s[i]=='0') zero++;			if(s[i]=='1') one++;		}		if(zero==one){			 cout <<0<< endl;		}		else if(zero==0 || one==0){			cout<<max(zero,one)<<endl;		}		else{			for(int i=0;i<n;i++){				if(s[i]=='1') zero--;				if(s[i]=='0') one--;				if(zero<0 or one<0){					cout<<n-i<<endl;					break;				}			}		}			    }     return 0;}